<?php$plugins->add_hook("index","hide_pag_add");function hide_pag_info(){return array("name" => "Hide pag","author" => "ionutvmi","author_site" => "http://dv.regup.ga","description" => "هذا وسوف إخفاء ترقيم الصفحات إذا كان هناك فقط الصفحة 1",);}function hide_pag_add($value){	global $total_results,$perpage;	if($total_results > 0 && ceil($total_results/$perpage) < 2)		$value = preg_replace("~<div class=\"pages\">([^<]+)</div>~s","",$value);	return $value;}?>